    reason: `❌ WRONG: Expected "${expected}" but got "${actual}"\n` +
            `   Model said: ${obj.title || 'N/A'}\n` +
            `   Rationale: ${obj.rationale?.substring(0, 150) || 'N/A'}...\n` +
            `   CWE: ${obj.cwe || 'none detected'}\n` +
            `   💡 FIX: Review prompt or provide better examples for this vulnerability type`
  };
}
